RestConnector.RazorFunctions:

string Prefix() {
  return Model.Scope=="page_to_page" ? "apu" :
         Model.Scope=="phrase_to_subdomain" ? "apf" :
         Model.Scope=="phrase_to_domain" ? "app" :
         Model.Scope=="term_to_page" ? "atu" :
         Model.Scope=="term_to_subdomain" ? "atf" :
         /*term_to_domain*/ "atp";
}

